
  create table "public"."play_data" (
    "id" bigint generated by default as identity not null,
    "user_id" uuid not null default auth.uid(),
    "updated_at" timestamp with time zone not null default now(),
    "video_id" text not null,
    "position" bigint default '0'::bigint,
    "duration" bigint default '0'::bigint
      );


alter table "public"."play_data" enable row level security;

CREATE UNIQUE INDEX play_data_pkey ON public.play_data USING btree (id);

CREATE UNIQUE INDEX play_data_user_video_unique ON public.play_data USING btree (user_id, video_id);

alter table "public"."play_data" add constraint "play_data_pkey" PRIMARY KEY using index "play_data_pkey";

alter table "public"."play_data" add constraint "play_data_user_id_fkey" FOREIGN KEY (user_id) REFERENCES public.profiles(uuid) not valid;

alter table "public"."play_data" validate constraint "play_data_user_id_fkey";

alter table "public"."play_data" add constraint "play_data_user_video_unique" UNIQUE using index "play_data_user_video_unique";

