  create policy "Users can insert own play_data"
  on "public"."play_data"
  as permissive
  for insert
  to public
with check ((auth.uid() = user_id));



  create policy "Users can read own play_data"
  on "public"."play_data"
  as permissive
  for select
  to public
using ((auth.uid() = user_id));



  create policy "Users can update own play_data"
  on "public"."play_data"
  as permissive
  for update
  to public
using ((auth.uid() = user_id))
with check ((auth.uid() = user_id));



